import React, { useState } from "react";

function App() {
  const [value, setValue] = useState('TEXT IN INPUT')

  return (
    <div className="App">
      <h2>{value}</h2>
      <input 
        type="text" 
        value={value}
        onChange={event => setValue(event.target.value)}
      />
    </div>
  );
}

export default App;
